import { album } from "./data/album";
import { useRef, useState, useEffect } from "react";
import "./App.css";
import { API_BASE } from "./config";
import UploadImage from "./component/UploadImage"; // üëà nuevo import

function ColorPicker() {
  const handleColorChange = (e) => {
    document.documentElement.style.setProperty("--accent-color", e.target.value);
  };

  return (
    <div className="color-picker">
      <label htmlFor="accentColor">Eleg√≠ un color üíñ: </label>
      <input
        type="color"
        id="accentColor"
        name="accentColor"
        defaultValue="#f8c8dc"
        onChange={handleColorChange}
      />
    </div>
  );
}

function App() {
  const [extraFotos, setExtraFotos] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [showLetter, setShowLetter] = useState(false);
  const [indiceFrase, setIndiceFrase] = useState(null);
  const [showGallery, setShowGallery] = useState(false);
  const [currentSrc, setCurrentSrc] = useState(null);

  // Ajustes
  const [showSettings, setShowSettings] = useState(false);
  const [showExtraGallery, setShowExtraGallery] = useState(false);

  const frases = [
    "HAGAMOS QUE ESTO FUNCIONE Y NO POR QUE SEA FACIL EHH, SINO POR QUE VALE LA PENA üíò",
    "Tu sonrisa es la calma para mis tormentas ‚ú®",
    "Eres mi raz√≥n de ser üíï",
    "Cada momento contigo es algo unico e inolvidable üéÅ",
    "Te amo m√°s de lo que las palabras puedan expresar üíñ",
    "Eres mi sue√±o hecho realidad üåô",
    "Contigo todo es magia ‚ú®",
    "Eres mi refugio y mi alegr√≠a üåπ",
    "Cada latido me recuerda que te amo üíò"
  ];

  // Cargar fondo y galer√≠a desde backend
  useEffect(() => {
    async function loadData() {
      try {
        const [bgRes, galRes] = await Promise.all([
          fetch(`${API_BASE}/background`).then((r) => r.json()),
          fetch(`${API_BASE}/gallery`).then((r) => r.json()),
        ]);

        if (bgRes?.url) {
          document.querySelector(".app").style.backgroundImage = `url(${bgRes.url})`;
        }
        if (galRes?.images) {
          setExtraFotos(galRes.images);
        }
      } finally {
        setIsLoading(false);
      }
    }
    loadData();
  }, []);

  const mostrarFraseAleatoria = () => {
    const indice = Math.floor(Math.random() * frases.length);
    setIndiceFrase(indice);
  };

  const mostrarSiguienteFrase = () => {
    setIndiceFrase((prev) => (prev === null ? 0 : (prev + 1) % frases.length));
  };

  const togglePlay = () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (audio.paused) {
      audio.play().then(() => setIsPlaying(true)).catch(() => {});
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  return (
    <div className="app">
      {/* Bot√≥n de ajustes */}
      <button
        className="btn-ajustes"
        onClick={() => setShowSettings(!showSettings)}
        aria-label="Abrir ajustes"
      >
        ‚öôÔ∏è
      </button>

      {/* Panel de ajustes */}
      {showSettings && (
        <div className="ajustes-panel">
          <button className="ajustes-close" onClick={() => setShowSettings(false)}>‚ùå</button>
          <h3>‚öôÔ∏è Ajustes</h3>

          <strong>üñºÔ∏è Fondo de pantalla:</strong>
          <UploadImage type="fondo" backendUrl={API_BASE} />

          <div style={{ marginTop: "15px" }}>
            <strong>üì∑ Agregar foto a galer√≠a:</strong>
            <UploadImage type="galeria" backendUrl={API_BASE} />
          </div>

          <div style={{ marginTop: "15px" }}>
            <button onClick={() => setShowExtraGallery(!showExtraGallery)}>
              <strong>{showExtraGallery ? "üì∑ Cerrar galer√≠a extra" : "üì∑ Abrir galer√≠a extra"}</strong>
            </button>
          </div>
        </div>
      )}

      {/* Galer√≠a extra */}
      {showExtraGallery && (
        <div className="extra-gallery">
          <h2>Galer√≠a de recuerdos üåü</h2>
          {isLoading ? (
            <p>Cargando...</p>
          ) : (
            <div className="gallery-grid">
              {extraFotos.map((src, index) => (
                <img key={index} src={src} alt={`Recuerdo ${index + 1}`} />
              ))}
            </div>
          )}
        </div>
      )}

      <h1>Para vos, mi amor üíï</h1>

      <button className="btn" onClick={mostrarFraseAleatoria}>
        Frase aleatoria üßû‚Äç‚ôÇÔ∏è
      </button>
      <button className="btn" onClick={mostrarSiguienteFrase}>
        Siguiente frase ‚è≠Ô∏è
      </button>

      {indiceFrase !== null && (
        <div className="frase-actual">{frases[indiceFrase]}</div>
      )}

      <button className="btn" onClick={togglePlay}>
        {isPlaying ? "‚è∏Ô∏è Pausar m√∫sica" : "üéµ Escuchar Tu Poeta"}
      </button>

      <button className="btn" onClick={() => setShowLetter(!showLetter)}>
        {showLetter ? "Cerrar carta üíå" : "Ver carta üíå"}
      </button>

      {showLetter && (
        <div className="carta-container">
          <div className="carta">
            <h2>Para vos, mi amor üíñ</h2>
            <p>
              Hay momentos de la vida que llega como un rayo y lo cambia todo en un instante...
              {/* Aqu√≠ va tu carta completa */}
            </p>
            <p className="firma">Con todo el amor del mundo, Juan ‚ú®</p>
          </div>
        </div>
      )}

      <button className="btn" onClick={() => setShowGallery(!showGallery)}>
        {showGallery ? "Cerrar √°lbum secreto üìÅ" : "Abrir √°lbum secreto üìÅ"}
      </button>

      {showGallery && (
        <div className="album">
          <h2>La Reina y el Poeta</h2>
          <div className="album-grid">
            {album.map((item) => (
              <div key={item.id} className="album-item">
                <img src={item.foto.src} alt={item.foto.alt} />
                <button
                  className="btn-cora"
                  onClick={() => {
                    if (audioRef.current) {
                      audioRef.current.pause();
                      setCurrentSrc(item.musica.src);
                      audioRef.current.src = item.musica.src;
                      audioRef.current.load();
                      audioRef.current.oncanplaythrough = () => {
                        audioRef.current.play().catch(() => {});
                      };
                    }
                  }}
                >
                  MI CORA ‚ù§Ô∏è
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Reproductor oculto */}
      <audio
        ref={audioRef}
        src={currentSrc}
        preload="auto"
        style={{ display: "none" }}
      />

      {/* Selector de colores */}
      <ColorPicker />

      <footer>
        <p>HECHO CON TODO EL AMOR DEL UNIVERSO POR TU POETA‚ú®</p>
      </footer>
    </div>
  );
}

export default App;
