import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import FraseCard from "./components/FraseCard";
import { frases, palabrasLindas } from "./data/frases";

export default function App() {
  const [indice, setIndice] = useState(0);
  const [modoLista, setModoLista] = useState(true);

  const fraseActual = frases[indice];

  const siguiente = () => setIndice((i) => (i + 1) % frases.length);
  const anterior = () => setIndice((i) => (i - 1 + frases.length) % frases.length);
  const aleatoria = () => setIndice(Math.floor(Math.random() * frases.length));

  //  M煤sica
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const audio = new Audio("/tu-poeta.mp3"); // archivo en carpeta public
    audio.loop = true;
    audio.volume = 0.5;
    audioRef.current = audio;

    // Intento de autoplay
    audio.play()
      .then(() => setIsPlaying(true))
      .catch(() => {
        setIsPlaying(false);
        console.log("Autoplay bloqueado, se necesita interacci贸n.");
      });

    return () => {
      audio.pause();
      audioRef.current = null;
    };
  }, []);

  const togglePlay = async () => {
    const audio = audioRef.current;
    if (!audio) return;

    if (audio.paused) {
      try {
        await audio.play();
        setIsPlaying(true);
      } catch {
        console.log("El navegador bloque贸 el play. Intenta de nuevo.");
      }
    } else {
      audio.pause();
      setIsPlaying(false);
    }
  };

  return (
    <div
      className="app"
      style={{
        backgroundImage: "url('/fondo.jpg')", // foto en carpeta public
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh"
      }}
    >
      <h1>Para vos, mi amor </h1>

      <div className="subtitulo">
        Palabras lindas: {palabrasLindas.join(" 路 ")}
      </div>

      <div className="controles">
        <button onClick={aleatoria}>Frase aleatoria</button>
        <button onClick={() => setModoLista(!modoLista)}>
          {modoLista ? "Ver una por vez" : "Ver lista completa"}
        </button>
        <button onClick={togglePlay}>
          {isPlaying ? "革 Pausar m煤sica" : "讹 Reproducir Tu Poeta"}
        </button>
      </div>

      {modoLista ? (
        <div className="lista">
          {frases.map((f, i) => (
            <FraseCard key={i} texto={f} />
          ))}
        </div>
      ) : (
        <div className="una-frase">
          <FraseCard texto={fraseActual} />
          <div className="paginacion">
            <button onClick={anterior}>Anterior</button>
            <button onClick={siguiente}>Siguiente</button>
          </div>
        </div>
      )}

      <footer className="footer">
        Hecho con mucho amor por Juan 
      </footer>
    </div>
  );
}
